        static void moveHero(ConsoleKeyInfo keyInfo)
        {
            for (int x = step5.Count - 1; x >= 0; x--)
            {
                for (int y = 0; y < step5[x].Length; y++)
                {
                    if (step5[x][y] == hero)
                    {
                        if (keyInfo.Key == ConsoleKey.UpArrow)
                        {
                            if ((x - 1) >= 0 && step5[x - 1][y] == emptyCell)
                            {
                                step5[x][y] = emptyCell;
                                step5[x - 1][y] = hero;
                                return;
                            }
