                                step5[x][y] = hero;
                                random();

                                return;
                            }
                            else if ((x - 1) >= 0 && step5[x - 1][y] == target7)
                            {
                                step5[x][y] = emptyCell;
                                step5[x][y] = hero;
                                random();

                                return;
                            }
                        }
                        else if (keyInfo.Key == ConsoleKey.DownArrow)
                        {
                            if ((x + 1) <= (step5.Count - 1) && step5[x + 1][y] == emptyCell)
                            {
                                step5[x][y] = emptyCell;
                                step5[x + 1][y] = hero;
                                return;
                            }
                            else if ((x + 1) <= (step5.Count - 1) && step5[x + 1][y] == target)
                            {
                                step5[x][y] = emptyCell;
                                step5[x + 1][y] = hero;
                                New_level();
                                return;
                            }
                        }
                        else if (keyInfo.Key == ConsoleKey.LeftArrow)
                        {
                            if ((y - 1) >= 0 && step5[x][y - 1] == emptyCell)
                            {
                                step5[x][y] = emptyCell;
                                step5[x][y - 1] = hero;
                                return;
                            }
                            else if ((y - 1) >= 0 && step5[x][y - 1] == target)
                            {
                                step5[x][y] = emptyCell;
                                step5[x][y - 1] = hero;
                                New_level();
                                return;
                            }
                            else if ((y - 1) >= 0 && step5[x][y - 1] == target2)
                            {
                                step5[x][y] = emptyCell;
                                step5[x][y ] = hero;
                                random();
                                return;
                            }
                        }
                        else if (keyInfo.Key == ConsoleKey.RightArrow)
                        {
                            if ((y + 1) <= (step5[x].Length - 1) && step5[x][y + 1] == emptyCell)
                            {
                                step5[x][y] = emptyCell;
                                step5[x][y + 1] = hero;
                                return;
                            }
                            else if ((y + 1) <= (step5[x].Length - 1) && step5[x][y + 1] == target)
                            {
                                step5[x][y] = emptyCell;
                                step5[x][y + 1] = hero;
                                New_level();
                                return;
                            }
                            else if ((y + 1) <= (step5[x].Length - 1) && step5[x][y + 1] == target3)
                            {
                                step5[x][y] = emptyCell;
                                step5[x][y] = hero;
                                Stepend();
                                return;
                            }
                            else if ((y + 1) <= (step5[x].Length - 1) && step5[x][y + 1] == target6)
                            {
                                step5[x][y] = emptyCell;
                                step5[x][y] = hero;
                          //      New_le(); Лаборатория 
                                return;
                            }
                        }
                    }
                }
            }
        }



    }
